import React, { Component } from 'react';
import './Blayout.css'

class Blayout extends  Component {

    	state = {
    		items: this.props.items || [],
    		showItem: true,
    		selectedItem: this.props.items 
    	}

    	OnDropdown = () => {
    		this.setState(prevState =>({
    			showItem: !prevState.showItem
    		}))
    	}

    	selectItem = (item) => this.setState({
    		selectedItem: item,
    		showItem: false

    	})


    render() {
    	const items  = this.state.items;
    	return<div>
    	       <div className = 'box-arrow' onClick={this.OnDropdown}>

    	       <span className = {`${this.state.showItem ? 'box-arrow-up' : 'box-arrow-down'}`}/>

    	       </div>

               <div style = {{display: this.state.showItem ? 'block' : 'none'}}>
               	{
               		items.map(item => <div className = {this.state.selectItem === item ? 'selected' : ''} key = {item.id} onClick ={() => this.selectItem(item)}>
               				{
               					item.value
               				}
               			</div>
               		)
               	}
               	</div>
               </div>


    }


}


export default Blayout;
