import React, { Component } from 'react';
import Blayout from './blayout/Blayout'
import Media from './media/Media'
import './App.css';

class App extends Component {
  constructor(){
  super()

  this.state = {
       bg:''
  }

}
componentWillUpdate(){
  this.setState({bg:'BarberShop'})



}


  render() {
    return (
      <div   className = {`${this.state.bg === 'BarberShop' ? 'barber' : 'salon'}`} >
        <Blayout 
          items = {[
            {value: 'BarberShop', id: 0},
            {value: 'ParloSalon', id: 1},
            {value: 'TattoParlo', id: 2},


            ]}
            ref = "child"

            bground = {this.onBGroundChange}

        />
        <Media />
      </div>
    );
  }
}

export default App;
