import React, { Component } from 'react';
import Logo from '../logo/Logo'
import './HomePage.css'




class HomePage extends Component {

	constructor(props){
		super(props)
		this.state = {
				turns: this.props.turns,
				showBtn: false,
				toDelete: '',

		}
	}


   onShowBtn = (e) =>{
   	 this.setState({showBtn:true, toDelete: e.target.id})

   	 console.log(this.state.toDelete)
   	 console.log(this.state.turns);
   }



    render()
    {
    	const turns = this.state.turns;
    		return(
    	<div className = 'container'>
    		   <div className = 'nav'>
    		      <div className = 'content'>
    		   </div>
    		   <div className = 'logo'>
    		      <Logo onRouteChange = {this.props.onRouteChange} />
    		   </div>
		       </div>
		<div className = 'home'>

				<div className = 'coments'>

				</div>		
				<div className = 'turns'>

		         { turns.map((turn,i)  =><li  className = 'turn' key = {i}>
		              <div 
		              className = 'selected-turn'
		              id = {turn.value} 
		              onClick = {this.onShowBtn}>
		              {turn.value}
		              </div>
		         	</li>)

		     }


				</div>
				<button>hola</button>
	    </div>
     </div>


		);
    }




}

export default HomePage;
