import React from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';


const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)

class AppModal  extends React.Component {
  constructor() {
    super();

    this.state = {
      modalIsOpen: false,
      types: '',
      being: '',
      vip: ''
    };

    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
  }


  componentDidMount(){
        Modal.setAppElement('body');
      fetch('http://localhost:3001/type')
      .then(res => res.json())
      .then(types => {
        this.setState({types:types});
        console.log(this.state.types);


      });
            fetch('http://localhost:3001/being')
      .then(res => res.json())
      .then(being => {
        this.setState({being:being});
      });

        fetch('http://localhost:3001/vip')
      .then(res => res.json())
      .then(vip => {
        this.setState({vip:vip});
      });

  }


  openModal() {
    this.setState({modalIsOpen: true});
  }

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }

  render() {
    return (
      <div>
        <button onClick={this.openModal} className = 'b ph3 pv2 input-reset ba b--white bg-transparent grow pointer white f6 dib'>+</button>
        <Modal 
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >

          <h2 ref={subtitle => this.subtitle = subtitle}>Add New Turn</h2>
          <button onClick={this.closeModal}>close</button>
         <div>
           <label>Name</label>
           <input type = 'text'   className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"/>
           <div>{this.state.types.id}</div> 
         </div>
        </Modal>
      </div>
    );
  }
}

export default AppModal;
