import React, { Component } from 'react';
import Blayout from './components/blayout/Blayout';
import Media from './components/media/Media';
import Signin from './components/signin/Signin';
import NewUser from './components/newuser/NewUser';
import NewHost from './components/newhost/NewHost';
import HomePage from './components/homepage/HomePage';
import SigninHost from './components/signinhost/SigninHost';
import UserPage from './components/userpage/UserPage';
import Particles from 'react-particles-js';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import './App.css';

const particleOps = {
        particles: {
          number:{
            value: 100,
            density: {
             enable: true,
             value_area:800
            }
          }
        }
       }

class App extends Component {
  constructor(){
  super()
      this.state = {
           route: 'blayout',
           isSignIn: false,
           user: [],
           host: [],
           turns: []
      }

}

 componentDidMount(){
  fetch('http://localhost:3001/turns' , {
    method: 'get',
    Headers: {"Content-Type" : "aplication/json"}
  }).then(res => res.json()).then(turns =>{
    this.setState({turns: turns})
  })
 }


onLoaduser = (user) => {
  this.setState({user: user})
}

onLoadhost = (host) => {
  this.setState({host: host})
}



onRouteChange = (route) =>{
  if(this.state.route === 'blayout'){
    this.setState({isSignIn: true})
  } else if(this.state.route === 'signout'){
    this.setState({isSignIn:false})
  }
    this.setState({route:route})
}


  render() {
    switch(this.state.route){
      case 'blayout':
      return <div className = 'barber'>
                      <Particles className='particles' 
              params={particleOps}
           />
              <Media onRouteChange = {this.onRouteChange} />
              <Blayout 
                  onRouteChange = {this.onRouteChange}
                  onBgChange = {this.onBgChange}
              />
              <div className = {'media2'}>
              </div>
            </div>;
      case 'home':
      return <div className = 'barber'>
              <Particles className='particles' 
              params={particleOps}
           />
                <HomePage onRouteChange = {this.onRouteChange} 
                   turns = {this.state.turns}
                   info = {this.state.host}
                  />
             </div>;
      case 'signout':
      return <div className = 'barber'>
              <Particles className='particles' 
              params={particleOps}
           />
                <Signin
                   onRouteChange = {this.onRouteChange}
                   onLoaduser = {this.onLoaduser}
               />
              </div>;
      case 'newuser':
      return <div className = 'barber'>
              <Particles className='particles' 
              params={particleOps}
           />
              <NewUser  onRouteChange = {this.onRouteChange} />
            </div>;
      case 'signinhost':
      return <div className = 'barber'>
              <Particles className='particles' 
              params={particleOps}
           />
              <SigninHost onLoadhost = {this.onLoadhost}  onRouteChange = {this.onRouteChange} />
            </div>;
      case 'newhost':
      return <div className = 'barber'>
              <Particles className='particles' 
              params={particleOps}
           />
                <NewHost onRouteChange = {this.onRouteChange} />
             </div>;
      case 'userpage':
      return <div className = 'barber'>
              <Particles className='particles' 
              params={particleOps}
           />
                <UserPage 
                  places = {this.state.host}


                />
             </div>
               }

                  }

}

export default App;
