import React, { Component } from 'react';
import Logo from '../logo/Logo'
import ServerProfile from '../serverprofile/ServerProfile';
import AppModal from '../modal/AppModal'
import Scroll from '../Scroll'
import './HomePage.css'




class HomePage extends Component {

	constructor(props){
		super(props)
		this.state = {
				turns: this.props.turns,
				showBtn: false,
				toDelete: '',
				newArray: [],
				index:'',
				addTurn: '',
        infoWanted: []

		}
	}

  componentDidMount(){
    fetch('http://localhost:3001/infoWanted/infosend')
    .then(res => res.json())
    .then(data => {
      this.setState({infoWanted: data})
      console.log(data)})
  }


   onShowBtn = (e) =>{
   	 this.setState({showBtn:true, toDelete: e.target.outerText, index: e.target.id});
     console.log(this.state.toDelete)
   }

   onDeleteHandler = (turn) => {
        this.setState({
        	newArray: delete turn[this.state.index]
        })
   }

   onTurnChange = (e) =>{this.setState({addTurn: e.target.value}) 
   console.log(this.state.addTurn)
}

   onAddTurns = () =>{
   	fetch('http://localhost:3001/addturns', {
   		method : 'post',
   		headers : {'Content-Type': 'application/json'},
   		body : JSON.stringify({
   			turn : this.state.addTurn
   		})
   	}).then(res => res.json())
   	  .then(newturn => {
   	  	this.setState({turns: newturn});

   	  	console.log(newturn)});

   }


    render()
    {
      const wanted = this.state.infoWanted.map((wanted, i) =>{
                         if(this.state.toDelete === wanted.userinfo){
                                return (
                                  <div key= {i}>
                                      <div key ={1}>{wanted.userinfo}</div>
                                      <div key ={2}>{wanted.typeinfo}</div>
                                      <div key ={3}>{wanted.beinginfo}</div>
                                      <div key ={4}>{wanted.vipinfo}</div>
                                  </div>
                                  );
                                 }
                     })

    	const turns = this.state.turns;
    	const { onDeleteHandler } = this;
    		return(

    	<div className = 'container'>
    		   <div className = 'nav'>
    		      <div className = 'profile'>
    		      <div className = 'profile-box'>
    		      <ServerProfile info = {this.props.info} />
    		      </div>
    		   </div>
    		   <div className = 'logo'>
    		      <Logo onRouteChange = {this.props.onRouteChange} />
    		   </div>
		       </div>
		<div className = 'home'>

				<div className = 'coments'>
                 <div className = 'addturns'>
                     <button onClick = {this.onAddTurns} className = 'b ph3 pv2 input-reset ba b--white bg-transparent grow pointer white f6 dib'>+</button>
                     <button onClick = {() => onDeleteHandler(turns)} className = 'b ph3 pv2 input-reset ba b--white bg-transparent grow pointer white f6 dib'>$</button>
                     <button onClick = {() => onDeleteHandler(turns)} className = 'b ph3 pv2 input-reset ba b--white bg-transparent grow pointer white f6 dib'>-</button>
                 </div>    
                     <div className = 'toshow'>
                       {wanted}
                     </div>
				</div>	

				<div className = 'turns-container'>
        <Scroll>
          <div className = 'turns'>
  		         { turns.map((turn,i)  =><li  className = 'turn' key = {i}>
  		              <div 
  		              className = {this.state.toDelete === turn.name ? 'selected-turn' : 'turn'}
  		              id = {i} 
  		              onClick = {this.onShowBtn}>
  		              {turn.name}
  		              </div>
  		         	</li>)

  		         }
               </div>  
               </Scroll> 

		        <div className = 'none'>
                             <button className = 'myButton' onClick = {() => onDeleteHandler(turns)}>...</button>  
                             <button className = 'myButton-2' onClick = {() => onDeleteHandler(turns)}>...</button>  
             </div>

             <div><AppModal /></div>



				</div>

	    </div>
     </div>


		);
    }




}

export default HomePage;
