import React, { Component } from 'react';
import '../dropdown/dropdown.css'

class DropDown extends  Component {

	constructor(props){
		super(props)

		 	this.state = {
    		items: this.props.items || [],
    		showItem: false,
    		selectedItem: this.props.items && this.props.items[0],

    	}

	}


    	OnDropdown = () => {
    		this.setState(prevState =>({
    			showItem: !prevState.showItem
    		}))
    	}

    	selectItem = (item) => {

    		this.setState({
    		selectedItem: item,
    		showItem: false

    	   })


    }




    render() {
    	const items  = this.state.items;
    	return(
    	<div >
	    	<div className = 'container'>
	    	    	<div className = 'select-box--box'>
		    	      <div className = 'container--box'>

		    	        <div className = 'selected--item' >
		    	           {
		    	           	this.state.selectedItem.value
		    	           }

		    	        </div>
		    	      	<div className = 'box-arrow' onClick={this.OnDropdown}>

			    	       <span className = {`${this.state.showItem ? 'box-arrow-up' : 'box-arrow-down'}`}/>

		    	        </div>

		                <div className = 'selected-box--items' style = {{display: this.state.showItem ? 'block' : 'none'}}>
		               	{
		               		items.map(item => <div 
		               			className = {this.state.selectItem === item ? 'selected' : ''} 
		               			key = {item.id}  
		               			onClick ={() => this.selectItem(item)}>
		               				{
		               					item.value	
		               				}
		               			</div>
		               		)
		               	}

		               	</div>

		    	      </div>
	               </div>

	    	</div>
    	</div>
    	)



    }


}


export default DropDown;
