import React, { Component } from 'react';
import Blayout from './components/blayout/Blayout';
import Media from './components/media/Media';
import Signin from './components/signin/Signin';
import NewUser from './components/newuser/NewUser';
import './App.css';

class App extends Component {
  constructor(){
  super()
      this.state = {
           bg:'BarberShop',
           route: 'home',
           isSignIn: false,
      }

}

onBgChange = (data) => {
  this.setState({
    bg: data.value
  })
}

onRouteChange = (data) =>{
  if(this.state.route === 'blayout'){
    this.setState({isSignIn: true})
  } else if(this.state.route === 'signout'){
    this.setState({isSignIn:false})
  }
    this.setState({route:data})
}



  render() {
    return (
      <div className = {`${this.state.bg === 'BarberShop' ? 'barber' : 'salon'}`} >
        {
          this.state.route === 'blayout' ?
              <div>
                <Blayout 
                  items = {[
                    {value: 'BarberShop', id: 0},
                    {value: 'ParloSalon', id: 1},


                    ]}

                    onBgChange = {this.onBgChange}

                />
                <Media onRouteChange = {this.onRouteChange} />
                </div>
                :
                (this.state.route === 'signout' 
                  ?<Signin onRouteChange = {this.onRouteChange} />
                  :<NewUser  onRouteChange = {this.onRouteChange} />
                  )
              }
      </div>
    );
  }
}

export default App;
