import React, { Component } from 'react';
import Logo from '../logo/Logo'
import ServerProfile from '../serverprofile/ServerProfile'
import './HomePage.css'




class HomePage extends Component {

	constructor(props){
		super(props)
		this.state = {
				turns: this.props.turns,
				showBtn: false,
				toDelete: '',
				newArray: [],
				index:'',
				addTurn: ''

		}
	}


   onShowBtn = (e) =>{
   	 this.setState({showBtn:true, toDelete: e.target.outerText, index: e.target.id});
   }

   onDeleteHandler = (turn) => {
        this.setState({
        	newArray: delete turn[this.state.index]
        })
   }

   onTurnChange = (e) =>{this.setState({addTurn: e.target.value}) 
   console.log(this.state.addTurn)
}

   onAddTurns = () =>{
   	fetch('http://localhost:3001/addturns', {
   		method : 'post',
   		headers : {"Tontent-Type" : "application/react"},
   		body : JSON.stringify({
   			turn : this.state.addTurn
   		})
   	}).then(res => res.json())
   	  .then(newturn => console.log(newturn));

   	  this.setState({turns: newturn})
   }


    render()
    {
    	const turns = this.state.turns;
    	const { onDeleteHandler } = this;
    		return(

    	<div className = 'container'>
    		   <div className = 'nav'>
    		      <div className = 'profile'>
    		      <div className = 'profile-box'>
    		      <ServerProfile info = {this.props.info.workers} />
    		      </div>
    		   </div>
    		   <div className = 'logo'>
    		      <Logo onRouteChange = {this.props.onRouteChange} />
    		   </div>
		       </div>
		<div className = 'home'>

				<div className = 'coments'>
                     <p onClick = {this.onAddTurns} className = 'mr3 f8 link dim white db pointer'>ADD</p>
                     <input  onChange = {this.onTurnChange} type = 'text' className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"/>
				</div>	

				<div className = 'turns'>
		         { turns.map((turn,i)  =><li  className = 'turn' key = {i}>
		              <div 
		              className = {this.state.toDelete === turn.value ? 'selected-turn' : 'none'}
		              id = {turn.id} 
		              onClick = {this.onShowBtn}>
		              {turn.value}
		              </div>

		         	</li>)

		     }   
		        <div className = 'buttons'>
                  <button className = 'myButton' onClick = {() => onDeleteHandler(turns)}>...</button>	
                  <button className = 'myButton-2' onClick = {() => onDeleteHandler(turns)}>...</button>	
		     	</div>

				</div>

	    </div>
     </div>


		);
    }




}

export default HomePage;
