import React, { Component } from 'react';
import Blayout from './blayout/Blayout'
import Media from './media/Media'
import './App.css';

class App extends Component {
  constructor(){
  super()
      this.state = {
           bg:''
      }

}

onBgChange = (data) => {
  this.setState({
    bg: data
  })
console.log(data.value);
console.log(this.state.bg);
}



  render() {
    return (
      <div className = {`${this.state.bg === 'BarberShop' ? 'barber' : 'salon'}`} >
        <Blayout 
          items = {[
            {value: 'BarberShop', id: 0},
            {value: 'ParloSalon', id: 1},


            ]}

            onBgChange = {this.onBgChange}

        />
        <Media />
      </div>
    );
  }
}

export default App;
