import React, { Component } from 'react';
import Logo from '../logo/Logo';
import './userpage.css'

class UserPage extends Component{
	constructor(){
		super();
		this.state = {
			types: [],
			vip: [],
			being:[],
			typetoadd: '',
			descrition: '',

		}
	}

	componentDidMount(){
		  fetch('http://localhost:3001/type')
		  .then(res => res.json())
		  .then(types => {
		  	console.log(types.type);
		  	this.setState({types:types.type});
		  	this.setState({being:types.being});
		  	this.setState({vip:types.vip});


		  });

	}


		onChangeSelectionType = (e) =>{
			this.setState({
							typetoadd1: e.target.outerText
						});

		}

		onChangeSelectionbeing = (e) =>{
			this.setState({
							typetoadd2: e.target.outerText	
						});

		}

		onChangeSelectionvip = (e) =>{
			this.setState({
							typetoadd3: e.target.outerText

						});

		}

	render(){
		const  types = this.state.types;
		return(
			<div className = 'upage'>
			   <div className = 'nav'>
					<div className = 'user'>
						<div className = 'log'>
							<Logo />
						</div>
					</div>
				</div>

				<div className = 'selection'>
				    <div className = ' selection-box'>
				      <div>
							  {
							  	this.state.types.map((type, i) =><li className = 'type' key={i}>
							  		<div  onClick = {this.onChangeSelectionType}
							  		className = {this.state.typetoadd1 === type.value ? 'selected' : 'type'}
							  		>
							  		{type.value}
							  		</div>
							  		</li>)
							  }
		                </div>
		                <div>
						    {
							  	this.state.being.map((being, i) =><li className = 'type' key={i}>
							  		<div onClick = {this.onChangeSelectionType} 
							  		className = {this.state.typetoadd2 === being.value ? 'selected' : 'type'}>
							  		{being.value}
							  		</div>
							  		</li>)
							}
						</div>
					    <div>
						    {
							  	this.state.vip.map((vip, i) =><li className = 'type' key={i}>
							  		<div onClick = {this.onChangeSelectionType} 
							  		className = {this.state.typetoadd3 === vip.value ? 'selected' : 'type'}>
							  		{vip.value}
							  		</div>
							  		</li>)
							}
						</div>
					</div>

					<div className = 'submit'>
					   <input type = 'submit' className="b ph3 pv2 input-reset ba b--white bg-transparent grow pointer white f6 dib"/>
					</div>
				</div>

				<div className = 'coments-box'>
				<input className = 'input b ph3 pv2 input-reset ba b--white bg-transparent grow pointer white f6 dib' 
				       type = 'text' placeholder = 'This where you will dicribe your decided hair cut' />
				<div className = 'coments-box--box'>
				    <div>{this.state.typetoadd1}</div>
				    <div>{this.state.typetoadd2}</div> 
				    <div>{this.state.typetoadd3}</div> 

				</div>

				</div>


			</div>
			);
	}
}
export default UserPage;
