import React, { Component } from 'react';
import Blayout from './blayout/Blayout'
import Media from './media/Media'
import './App.css';

class App extends Component {
  constructor(props){
  super(props)

  this.state = {
       bg:''
  }

}

onBGroundChange = () =>{
  this.setState({bg:this.items.child.bg})

  console.log(this.state.bg);

}



  render() {
    return (
      <div onchange = {this.onBGroundChange()}  className = {`${this.state.bg.value === 'BarberShop' ? 'barber' : 'salon'}`} >
        <Blayout 
          items = {[
            {value: 'BarberShop', id: 0},
            {value: 'ParloSalon', id: 1},
            {value: 'TattoParlo', id: 2},


            ]}
            ref = {child}

            bground = {this.onBGroundChange}

        />
        <Media />
      </div>
    );
  }
}

export default App;
