import React, { Component } from 'react';
import Logo from '../logo/Logo'
import './HomePage.css'




class HomePage extends Component {

	constructor(props){
		super(props)
		this.state = {
				turns: this.props.turns,
				showBtn: false,
				toDelete: '',
				newArray: [],
				index:''

		}
	}


   onShowBtn = (e) =>{
   	 this.setState({showBtn:true, toDelete: e.target.id})

   	 console.log(this.state.toDelete)
   	 console.log(e.target.key)
   	 console.log(this.state.turns);
   }

   onDeleteHandler = (turn) => {
        this.setState({
        	newArray: delete turn[0]
        })
   }



    render()
    {
    	const turns = this.state.turns;
    	const { onDeleteHandler } = this;
    		return(

    	<div className = 'container'>
    		   <div className = 'nav'>
    		      <div className = 'content'>
    		   </div>
    		   <div className = 'logo'>
    		      <Logo onRouteChange = {this.props.onRouteChange} />
    		   </div>
		       </div>
		<div className = 'home'>

				<div className = 'coments'>

				</div>	
				<button onClick = {() => onDeleteHandler(turns)}>hola</button>	
				<div className = 'turn'>

		         { turns.map((turn,i)  =><li  className = 'turn' key = {i}>
		              <div 
		              className = {this.state.toDelete === turn.value ? 'selected-turn' : 'none'}
		              id = {turn.value} 
		              onClick = {this.onShowBtn}>
		              {turn.value}
		              </div>
		         	</li>)

		     }


				</div>

	    </div>
     </div>


		);
    }




}

export default HomePage;
