import React from 'react';
 import './menu.css'
class Settingsmenu extends React.Component {
  constructor() {
    super();
    this.state = {
        dpSelecte: 'Barber',
        show: false
    }
  }

    onSelect = (e) =>{
        this.setState({dpSelecte: e.target.outerText})
    }

    onShow = () => {
       this.setState(  (prevState => {
           return {show: !prevState.show}
        }))

        console.log(this.state.show)
  }



  render() {
    return (
         <div>
             <div className = 'd-box' onClick ={this.onShow}>{this.state.dpSelecte}  </div>
             <div className ={this.state.show ? 'show' : 'none'}>
                <div className = 'n-selected' onClick = {this.onSelect}>juan</div>
                <div className = 'n-selected'>pedro</div>
             </div>
        </div>
    );
  }
}

export default Settingsmenu;
